import './App.css';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import Particle from './components/Particles/Particles';
import { Component } from 'react';
import Clarifai from 'clarifai';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import { toHaveStyle } from '@testing-library/jest-dom/dist/matchers';

const app = new Clarifai.App({
    apiKey: "94fcec7fc44d4da5b7c0450d8d1bb734",
});  

class App extends Component {
	constructor() {
		super();
		this.state = {
			input: '',
			imageURL: '',
			boxesList: [

			],
		}
	}

	calculateFaceLocation = (data) => {
		// const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
		// const image = document.getElementById('inputimage');
		// const width = Number(image.width);
		// const height = Number(image.height);
		// return {
		// 	leftCol: clarifaiFace.left_col * width,
		// 	topRow: clarifaiFace.top_row * height,
		// 	rightCol: width - (clarifaiFace.right_col * width),
		// 	bottomRow: height - (clarifaiFace.bottom_row * height),
		// }
		const clarifaiFaces = data.outputs[0].data.regions;
		const clarifaiFacesList = clarifaiFaces.map(face => {
			return(face.region_info.bounding_box);
		});
		console.log(clarifaiFacesList);
	}

	displayFaceBox = (boxes) => {
		const container = document.getElementsByClassName('contains')[0];
		for(let i = 0; i < boxes.length(); i++) {
			let box = document.createElement('div');
			box.style.top = boxes[i].topRow;
			box.style.right = boxes[i].rightCol;
			box.style.left = boxes[i].leftCol;
			box.style.bottom = boxes[i].bottomRow;
		}
		this.setState({boxesList: boxes});
	}

	onInputChange = (event) => {
		this.setState({input: event.target.value})
	}

	onButtonSubmit = () => {
		this.setState({imageURL: this.state.input})
		app.models.predict(
		  Clarifai.FACE_DETECT_MODEL,
		  this.state.input,
		  )
		  .then(response => this.displayFaceBox(this.calculateFaceLocation(response)))
		  .catch((err) => console.log(err));
	}

	render() {
		return (
		<div className="App">
			<Particle className='particle-settings'/>
			<Navigation />
			<Logo />
			<Rank />
			<ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit}/>

			<FaceRecognition boxes={this.state.boxesList} imageURL={this.state.imageURL}/>
		</div>
		);
	}
}

export default App;
